
li({
  "class": "undefined"
}, function() {
  header({
    "class": "header"
  }, function() {
    return h3(this.post.toLabel());
  });
  dl({
    "class": "content"
  }, function() {
    dt("Title:");
    return dd(this.post.title);
  });
  return footer({
    "class": "footer"
  }, function() {
    return menu(function() {
      menuItem("Edit", editPostPath(this.post));
      return menuItem("Back", postsPath);
    });
  });
});
