
tableFor("posts", function(t) {
  t.head(function() {
    return t.row(function() {
      t.cell("title", {
        sort: true
      });
      t.cell();
      t.cell();
      return t.cell();
    });
  });
  t.body(function() {
    var post, _i, _len, _ref, _results;
    _ref = this.posts;
    _results = [];
    for (_i = 0, _len = _ref.length; _i < _len; _i++) {
      post = _ref[_i];
      _results.push(t.row(function() {
        t.cell(function() {
          return post.get("title");
        });
        t.cell(function() {
          return linkTo('Show', urlFor(post));
        });
        t.cell(function() {
          return linkTo('Edit', urlFor(post, {
            action: "edit"
          }));
        });
        return t.cell(function() {
          return linkTo('Destroy', urlFor(post), {
            method: "delete"
          });
        });
      }));
    }
    return _results;
  });
  return linkTo('New Post', urlFor(App.Post, {
    action: "new"
  }));
});
