var defaultUrlOptions, post;

post = null;

defaultUrlOptions = null;

describe('urlFor', function() {
  beforeEach(function() {
    Tower.Route.reload();
    post = new App.Post({
      id: 10
    });
    return defaultUrlOptions = {
      onlyPath: true,
      params: {},
      trailingSlash: false,
      host: "example.com"
    };
  });
  test('urlFor(App.Post, onlyPath: false)', function() {
    var path;
    path = Tower.urlFor(App.Post, _.extend(defaultUrlOptions, {
      onlyPath: false
    }));
    return assert.equal(path, "http://example.com/posts");
  });
  test('urlFor(App.Post, onlyPath: false, user: "lance", password: "pollard")', function() {
    var path;
    path = Tower.urlFor(App.Post, _.extend(defaultUrlOptions, {
      onlyPath: false,
      user: "lance",
      password: "pollard"
    }));
    return assert.equal(path, "http://lance:pollard@example.com/posts");
  });
  test('urlFor(App.Post)', function() {
    var path;
    path = Tower.urlFor(App.Post, defaultUrlOptions);
    return assert.equal(path, "/posts");
  });
  test('urlFor(post)', function() {
    var path;
    path = Tower.urlFor(post, defaultUrlOptions);
    return assert.equal(path, "/posts/10");
  });
  test('urlFor("admin", post)', function() {
    var path;
    path = Tower.urlFor("admin", post, defaultUrlOptions);
    return assert.equal(path, "/admin/posts/10");
  });
  test('urlFor(post, likeCount: 10)', function() {
    var path;
    path = Tower.urlFor(post, _.extend(defaultUrlOptions, {
      params: {
        likeCount: 10
      }
    }));
    return assert.equal(path, "/posts/10?likeCount=10");
  });
  test('urlFor(post, likeCount: ">=": 10)', function() {
    var path;
    path = Tower.urlFor(post, _.extend(defaultUrlOptions, {
      params: {
        likeCount: {
          ">=": 10
        }
      }
    }));
    return assert.equal(path, "/posts/10?likeCount=10..n");
  });
  test('urlFor(post, likeCount: ">=": 10, "<=": 100)', function() {
    var path;
    path = Tower.urlFor(post, _.extend(defaultUrlOptions, {
      params: {
        likeCount: {
          ">=": 10,
          "<=": 100
        }
      }
    }));
    return assert.equal(path, "/posts/10?likeCount=10..100");
  });
  test('urlFor(post, likeCount: ">=": 10, "<=": 100)', function() {
    var path;
    path = Tower.urlFor(post, _.extend(defaultUrlOptions, {
      params: {
        likeCount: {
          ">=": 10,
          "<=": 100
        }
      }
    }));
    return assert.equal(path, "/posts/10?likeCount=10..100");
  });
  test('urlFor(post, title: "Hello World", likeCount: ">=": 10, "<=": 100)', function() {
    var path;
    path = Tower.urlFor(post, _.extend(defaultUrlOptions, {
      params: {
        title: "Hello World",
        likeCount: {
          ">=": 10,
          "<=": 100
        }
      }
    }));
    return assert.equal(path, "/posts/10?likeCount=10..100&title=Hello+World");
  });
  return test('urlFor(post, [{title: /Rails/}, {likeCount: ">=": 10}])', function() {});
});
