var describeWith, user;

user = null;

describeWith = function(store) {
  return describe("Tower.Model.Callbacks (Tower.Store." + store.name + ")", function() {
    return beforeEach(function(done) {
      App.User.store(new store({
        name: "users",
        type: "App.User"
      }));
      return done();
    });
  });
};

describeWith(Tower.Store.Memory);

if (Tower.client) {
  describeWith(Tower.Store.Ajax);
} else {
  describeWith(Tower.Store.MongoDB);
}
