var describeWith, user;

user = null;

describeWith = function(store) {
  return describe("Tower.Model.Indexing (Tower.Store." + store.name + ")", function() {
    beforeEach(function(done) {
      var _this = this;
      return store.clean(function() {
        App.Project.store(store);
        return done();
      });
    });
    test('.index()', function(done) {
      var indexes;
      indexes = App.Project.indexes();
      assert.ok(indexes.hasOwnProperty('titleIndexedWithMethod'));
      return done();
    });
    return test('index: true', function(done) {
      var indexes;
      indexes = App.Project.indexes();
      assert.ok(indexes.hasOwnProperty('titleIndexedWithOption'));
      return done();
    });
  });
};

if (!Tower.client) {
  describeWith(Tower.Store.MongoDB);
}
