
describe('Tower.Factory', function() {
  beforeEach(function() {
    return Tower.Factory.clear();
  });
  describe('.define', function() {
    test('metadata', function() {
      var expected, key, userFactory, value;
      Tower.Factory.define("user", function() {});
      expected = {
        name: 'user',
        className: 'App.User',
        parentClassName: void 0
      };
      userFactory = Tower.Factory.definitions["user"];
      for (key in expected) {
        value = expected[key];
        assert.equal(value, userFactory[key]);
      }
      return assert.ok(userFactory.hasOwnProperty("callback"));
    });
    test('#toClass', function() {
      var userFactory;
      userFactory = Tower.Factory.define("user", function() {});
      return assert.equal(userFactory.toClass().name, "User");
    });
    test('throw error if cant figure out class', function() {
      var userFactory;
      userFactory = Tower.Factory.define("guest", function() {});
      return assert.throws(function() {
        return userFactory.toClass().name;
      });
    });
    return test('with className', function() {
      var userFactory;
      userFactory = Tower.Factory.define("guest", {
        className: "User"
      }, function() {});
      assert.doesNotThrow(function() {
        return userFactory.toClass().name;
      });
      return assert.equal(userFactory.toClass().name, "User");
    });
  });
  return describe('.create', function() {
    test('create (factory has no attributes)', function() {
      var user;
      Tower.Factory.define("user", function() {});
      user = Tower.Factory.create("user");
      return assert.equal(user.constructor.name, "User");
    });
    test('create (factory has attributes)', function() {
      var user;
      Tower.Factory.define("user", function() {
        return {
          firstName: "Isaac"
        };
      });
      user = Tower.Factory.create("user");
      return assert.equal(user.get('firstName'), "Isaac");
    });
    return test('create (factory has async attributes)', function(done) {
      var _this = this;
      Tower.Factory.define("user", function(callback) {
        var respond;
        respond = function() {
          return callback(null, {
            firstName: "Isaac"
          });
        };
        return setTimeout(respond, 10);
      });
      return Tower.Factory.create("user", function(error, user) {
        assert.equal(user.get('firstName'), "Isaac");
        return done();
      });
    });
  });
});
