var fs, wrench;

wrench = require('wrench');

fs = require('fs');

Tower.Support.File = {
  removeDirectory: function(path, recursive) {
    if (recursive == null) {
      recursive = true;
    }
    if (recursive) {
      return wrench.rmdirSyncRecursive(path);
    } else {
      return fs.rmdirSync(path);
    }
  }
};
