var controller, user, view;

controller = null;

view = null;

user = null;

describe('Tower.View', function() {
  beforeEach(function() {
    return view = new Tower.View;
  });
  test('layout', function() {
    var template;
    template = function() {
      doctype(5);
      return html(function() {
        head(function() {
          meta({
            charset: "utf-8"
          });
          return title("Tower.js - Full Stack JavaScript Framework for Node.js and the Browser");
        });
        return body({
          role: "application"
        }, function() {});
      });
    };
    return view.render({
      template: template
    }, function(error, result) {
      return assert.equal(result, "<!DOCTYPE html>\n<html>\n  <head>\n    <meta charset=\"utf-8\" />\n    <title>Tower.js - Full Stack JavaScript Framework for Node.js and the Browser</title>\n  </head>\n  <body role=\"application\">\n  </body>\n</html>\n");
    });
  });
  return test('yields', function() {});
});

describe('Tower.View eco template', function() {
  beforeEach(function() {
    return view = new Tower.View;
  });
  return test('eco layout', function() {
    var template;
    template = function() {
      return "<!DOCTYPE html>\n<html>\n  <head>\n    <meta charset=\"utf-8\" />\n    <title>Tower.js - Full Stack JavaScript Framework for Node.js and the Browser</title>\n  </head>\n  <body role=\"application\">\n    <div>2</div>\n  </body>\n</html>";
    };
    return view.render({
      type: "eco",
      template: template
    }, function(error, result) {
      return assert.equal(result, "<!DOCTYPE html>\n<html>\n  <head>\n    <meta charset=\"utf-8\" />\n    <title>Tower.js - Full Stack JavaScript Framework for Node.js and the Browser</title>\n  </head>\n  <body role=\"application\">\n    <div>2</div>\n  </body>\n</html>");
    });
  });
});
